import React, { useState } from 'react';
import { motion } from 'framer-motion';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  async function sendMessage() {
    const res = await fetch("https://tindbot-production.up.railway.app/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message: input }),
    });
    const data = await res.json();
    setMessages([...messages, { from: "user", text: input }, { from: "bot", text: data.choices[0].message.content }]);
    setInput("");
  }

  return (
    <div className="h-screen flex flex-col items-center justify-center bg-primary p-6">
      <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-4 space-y-4">
        <h1 className="text-xl font-semibold text-center text-primary">Dr. Dropin Chatbot</h1>

        {/* Chat messages */}
        <div className="space-y-3">
          {messages.map((m, i) => (
            <motion.div
              key={i}
              className={`p-2 rounded-lg ${m.from === "user" ? "bg-blue-500 text-white" : "bg-gray-200 text-black"} transition-all duration-300 ease-in-out`}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
            >
              <strong>{m.from === "user" ? "You" : "Bot"}:</strong> {m.text}
            </motion.div>
          ))}
        </div>

        {/* Input field and send button */}
        <div className="flex">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
            placeholder="Ask something..."
          />
          <button
            onClick={sendMessage}
            className="ml-2 p-2 bg-primary text-white rounded-lg hover:bg-blue-700 focus:outline-none"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
